pragma solidity >=0.6.0;

import "./safemath.sol";

contract initializeGame {
    uint256 public totalPlayers = 0;
    uint256 public nonceForRand = 0;
    bool public startGame = false;

    /*first 2 numbers identify the card Type 
    (e.g if its 01 it will be a +1 point card)
    */

    //In order to simplify, addresses etc htaa denge but can add in later versions

    uint256 public cardModValue = 10000;
    address payable public owner;

    struct Player {
        uint256 balance;
        address playerAddr;
        uint256 round; //Makes sure everyone is on the smae round
    }

    Player[] public players;
    uint256[] public cards;
    mapping(uint256 => uint256) public cardToOwner;

    /*
    Will call player according to id
    planning on opening a new page for each player
    */
    using SafeMath for uint256;
    // using SafeMath32 for uint32;
    // using SafeMath16 for uint16;

    modifier onlyOwnerAllowed() {
        require(msg.sender == owner);
        _;
    }

    constructor() {
        owner = payable(msg.sender);
        totalPlayers = 0;
    }

    //Will add a player into the game

    function createPlayer() public {
        require(startGame == false);
        // uint256[] memory tempCards;
        // tempCards[0] = generateCards(cardModValue);
        players.push(Player(100, address(msg.sender), 1));
        cards.push(generateCards(cardModValue)); //Creates one card for player to start with

        cardToOwner[cards[totalPlayers]] = totalPlayers; //Assigns currentPlayer as the owner of card

        totalPlayers = totalPlayers.add(1); //Increments total players
    }

    function startTheGame() public onlyOwnerAllowed {
        startGame = true;
    }

    //used to Generate Cards for the game

    function generateCards(uint256 _cardModValue) public returns (uint256) {
        while (true) {
            uint256 newCard = uint256(
                keccak256(
                    abi.encodePacked(block.timestamp, msg.sender, nonceForRand)
                )
            ) % _cardModValue;

            //9560
            //9300

            if (checkCard(newCard) == true) {
                return newCard;
            }
            nonceForRand = nonceForRand.add(1);
        }
        return 12345678 % _cardModValue;
    }

    function checkCard(uint256 _newCard) public view returns (bool) {
        //check if key exists thn false
        if (cardToOwner[_newCard] != 0) {
            //means that it exists
            return false;
        }
        return true;
    }
}
